	void ColorCore::to_ostream(std::ostream &os) const
	{
	  os << "Color(";
	  getName().to_ostream(os);
	  getMin_range().to_ostream(os);
	  getMax_range().to_ostream(os);
	  os << ") ";
	}
	
	// convert to xml stream
	void ColorCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<name>";
		getName().to_xml(os, indent);
		os << indent << "</name>";
		os << indent << "<min_range>";
		getMin_range().to_xml(os, indent);
		os << indent << "</min_range>";
		os << indent << "<max_range>";
		getMax_range().to_xml(os, indent);
		os << indent << "</max_range>";
	}
	
	// restore from xml stream
	void ColorCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_name("<name>");
		static const Smart::KnuthMorrisPratt kmp_min_range("<min_range>");
		static const Smart::KnuthMorrisPratt kmp_max_range("<max_range>");
		
		if(kmp_name.search(is)) {
			CommObjectRecognitionObjects::Colors nameItem;
			nameItem.from_xml(is);
			setName(nameItem);
		}
		if(kmp_min_range.search(is)) {
			CommObjectRecognitionObjects::BGRSpace min_rangeItem;
			min_rangeItem.from_xml(is);
			setMin_range(min_rangeItem);
		}
		if(kmp_max_range.search(is)) {
			CommObjectRecognitionObjects::BGRSpace max_rangeItem;
			max_rangeItem.from_xml(is);
			setMax_range(max_rangeItem);
		}
	}
	
