	const char* HSVSpaceCore::getCompiledHash()
	{
		return CommObjectRecognitionObjectsIDL::REPO_HASH;
	}
	
	void HSVSpaceCore::getAllHashValues(std::list<std::string> &hashes)
	{
		// get own hash value
		hashes.push_back(getCompiledHash());
	}
	
	void HSVSpaceCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "HSVSpaceCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
	}
	
	#ifdef ENABLE_HASH
	size_t HSVSpaceCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		boost::hash_combine(seed, data.h);
		boost::hash_combine(seed, data.s);
		boost::hash_combine(seed, data.v);
		
		return seed;
	}
	#endif
	
	// default constructor
	HSVSpaceCore::HSVSpaceCore()
	:	idl_HSVSpace()
	{  
		setH(0.0);
		setS(0.0);
		setV(0.0);
	}
	
	HSVSpaceCore::HSVSpaceCore(const DATATYPE &data)
	:	idl_HSVSpace(data)
	{  }

	HSVSpaceCore::~HSVSpaceCore()
	{  }
	
	void HSVSpaceCore::to_ostream(std::ostream &os) const
	{
	  os << "HSVSpace(";
	  os << getH() << " ";
	  os << getS() << " ";
	  os << getV() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void HSVSpaceCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<h>" << getH() << "</h>";
		os << indent << "<s>" << getS() << "</s>";
		os << indent << "<v>" << getV() << "</v>";
	}
	
	// restore from xml stream
	void HSVSpaceCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_h("<h>");
		static const Smart::KnuthMorrisPratt kmp_s("<s>");
		static const Smart::KnuthMorrisPratt kmp_v("<v>");
		
		if(kmp_h.search(is)) {
			double hItem;
			is >> hItem;
			setH(hItem);
		}
		if(kmp_s.search(is)) {
			double sItem;
			is >> sItem;
			setS(sItem);
		}
		if(kmp_v.search(is)) {
			double vItem;
			is >> vItem;
			setV(vItem);
		}
	}
	
